import React, { Component } from 'react';
import Title from './components/Title';
import TOC from './components/TOC';
import ReadContent from './components/ReadContent';
import './App.css';

class App extends Component {
  constructor(props){
    super(props);
    this.max_content_id = 2;
    this.state = {
      mode: 'welcome',
      selected_id: 0,
      header: { title: "To DO List", sub: "written by Jerome" },
      welcome: { subTitle: "Basic React", subText: "welcome to this page programmed using React" },
      contents: [
        { id: 0, subTitle:"HTML", subText:"HTML is a basic markup language to learn about WEB" },
        { id: 1, subTitle:"CSS", subText:"CSS is Cascading Style Sheet language." },
        { id: 2, subTitle:"Javascript", subText:"Javascript is the basic language to deal with DOM in browser." }
      ]
    }
  }
  getReadContent() {
    for (let content of this.state.contents) {
      if (content.id === this.state.selected_id) {
        return content;
      }
    }
  }
  printContent() {
    let _subTitle, _subText, _article = null;
    if (this.state.mode === 'welcome') {
      // welcome 일 때 동작 수행하게
      _subTitle = this.state.welcome.subTitle;
      _subText = this.state.welcome.subText;
      _article = <ReadContent subTitle={_subTitle} subText={_subText}></ReadContent>;
    }
    else if (this.state.mode === 'read') {
      let _content = this.getReadContent();
      _subTitle = _content.subTitle;
      _subText = _content.subText;
      _article = <ReadContent subTitle={_subTitle} subText={_subText}></ReadContent>;

    }

    return _article;
  }

  render() {
    return (
      <div className="App">
        <Title
          title={this.state.header.title}
          sub={this.state.header.sub}
          onChangePage={function () {
            this.setState({ mode: 'welcome' });
          }.bind(this)}
        ></Title>

        <TOC
          data={this.state.contents}
          onChangePage={function (id) {
            this.setState({
              selected_id: Number(id),
              mode: 'read'
            });
          }.bind(this)}
        ></TOC>

        {this.printContent()}
      </div>
    );
  }
}

export default App;
